import React, { useState } from "react";
import "./App.css";

const App = () => {
  const [hiddenCards, setHiddenCards] = useState({});

  const handleClick = (event, video) => {
    event.preventDefault(); // Stop immediate redirect

    // Hide the card (trigger vaporize animation)
    setHiddenCards((prev) => ({ ...prev, [video.id]: true }));

    // Open YouTube after animation delay
    setTimeout(() => {
      window.open(video.url, "_blank");
    }, 1000); // delay YouTube open by 1 sec

    // Restore the card after 3 seconds
    setTimeout(() => {
      setHiddenCards((prev) => ({ ...prev, [video.id]: false }));
    }, 3000);
  };

  const videos = [
    {
      id: 1,
      title: "Intro to DevOps",
      url: "https://youtu.be/dQw4w9WgXcQ",
    },
    {
      id: 2,
      title: "CI/CD Explained",
      url: "https://youtu.be/Z7sFbHa_mBY",
    },
  ];

  return (
    <div className="app-container">
      <h1>DevOpsTube</h1>
      <p className="subtitle">Your CI/CD Learning Journey</p>
      <div className="video-list">
        {videos.map((video) => (
          <div
            key={video.id}
            className={`video-card ${
              hiddenCards[video.id] ? "vaporize" : ""
            }`}
          >
            <h3>{video.title}</h3>
            <a
              href={video.url}
              onClick={(e) => handleClick(e, video)}
            >
              â–º Watch on YouTube
            </a>
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
