import React, { useEffect, useState } from "react";
import "./App.css";

function App() {
  const [hiddenCards, setHiddenCards] = useState({});

  const handleClick = (id) => {
    // Hide the card temporarily
    setHiddenCards((prev) => ({ ...prev, [id]: true }));

    // Restore it after 3 seconds
    setTimeout(() => {
      setHiddenCards((prev) => ({ ...prev, [id]: false }));
    }, 3000);
  };

  const videos = [
    {
      id: 1,
      title: "Intro to DevOps",
      url: "https://youtu.be/dQw4w9WgXcQ",
    },
    {
      id: 2,
      title: "CI/CD Explained",
      url: "https://youtu.be/Z7sFbHa_mBY",
    },
  ];
  return (
    <div className="app-container">
      <h1>DevOpsTube</h1>
      <p className="subtitle">Your CI/CD Learning Journey</p>
      <div className="video-list">
        {videos.map((video) => (
          <div
            key={video.id}
            className={`video-card ${
              hiddenCards[video.id] ? "vaporize" : ""
            }`}
          >
            <h3>{video.title}</h3>
            <a href={video.url} target="_blank" rel="noopener noreferrer">
              <i className="fas fa-play-circle" style={{ marginRight: "8px" }}></i>
              Watch on YouTube
            </a>

          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
